/** @format */

import {Form, Formik} from "formik";
import {useState} from "react";
import * as Yup from "yup";
import Card from "../UI/Card/Card";
import classes from "./Login.module.css";

const Login = (props) => {
	const [isConnecting, setIsConnecting] = useState(false);

	const formikInitialValues = {
		tokenContactAddress: "",
		receiverAddress: "",
		amount: "",
	};

	const formikValidationSchema = Yup.object({
		fullName: Yup.string().required("Name can't be blank"),
		email: Yup.string()
			.email("Not a valid email")
			.required("Email can't be blank"),
		subject: Yup.string().required("Subject can't be blank"),
		message: Yup.string()
			.required("Message can't be blank")
			.max(500, "Must be 500 characters or less"),
	});

	const detectProvider = () => {
		let provider;
		if (window.ethereum) {
			provider = window.ethereum;
		} else if (window.web3) {
			provider = window.web3.currentProvider;
		} else {
			window.alert("No Ethereum browser detected! Check out MetaMask");
		}
		return provider;
	};

	const onLoginHandler = async () => {
		const provider = detectProvider();
		if (provider) {
			if (provider !== window.ethereum) {
				console.error(
					"Not window.ethereum provider. Do you have multiple wallet installed ?"
				);
			}
			setIsConnecting(true);
			await provider.request({
				method: "eth_requestAccounts",
			});
			setIsConnecting(false);
		}
		props.onLogin(provider);
	};

	return (
		<Card className={classes.login}>
			<Formik
				initialValues={formikInitialValues}
				validationSchema={formikValidationSchema}
			>
				<Form className={classes.form}>
					<input
						className={classes.formInput}
						name="tokenContactAddress"
						type="text"
						placeholder="Enter your token contact address"
						id="token_contact_address"
					/>
					<input
						className={classes.formInput}
						name="receiverAddress"
						type="text"
						placeholder="Enter the receiver address"
						id="receiver_address"
					/>
					<input
						className={classes.formInput}
						name="amount"
						type="number"
						placeholder="Enter amount"
						id="amount"
					/>

					<button
						onClick={onLoginHandler}
						className={classes.button}
						type="button"
					>
						{!isConnecting && "Connect"}
						{isConnecting && "Loading..."}
					</button>
				</Form>
			</Formik>
		</Card>
	);
};

export default Login;
